import cv2, tensorflow as tf, numpy as np; model = tf.keras.Sequential([tf.keras.layers.Conv2D(32, (3,3), activation='relu', input_shape=(28,28,1)), tf.keras.layers.MaxPooling2D(2,2), tf.keras.layers.Flatten(), tf.keras.layers.Dense(128, activation='relu'), tf.keras.layers.Dense(10, activation='softmax')]); (x_train, y_train), _ = tf.keras.datasets.mnist.load_data(); x_train = np.expand_dims(x_train, -1)/255.0; model.compile(optimizer='adam', loss='sparse_categorical_crossentropy', metrics=['accuracy']); model.fit(x_train, y_train, epochs=1)
